if (GoToTask.ToPosition(new Vector3(-9228.592, -2696.162, 89.08611)))
{
    while (Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause && Skill.GetValue(SkillLine.Lockpicking) < 75)
    {
        var obj = ObjectManager.GetObjectWoWGameObject()
            .FirstOrDefault(o => !wManager.wManagerSetting.IsBlackListed(o.Guid) && o.Entry == 178244 || o.Entry == 178245 || o.Entry == 178246);
        if (obj != null)
        {
if(ObjectManager.Me.Position.DistanceTo(obj.Position) > 3)
{
wManager.Wow.Bot.Tasks.GoToTask.ToPosition(obj.Position, 1f, false, context => Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause && !Conditions.IsAttackedAndCannotIgnore);

}
            Interact.InteractGameObject(obj.GetBaseAddress);
            Usefuls.WaitIsCastingAndLooting();
            Lua.LuaDoString("LootCloseButton:Click();");
            wManager.wManagerSetting.AddBlackList(obj.Guid, 5000, true);
        }
        
    }
}
return true;

